"use strict";

const ensure = require("util/ensure");

module.exports = class HttpResponse {

	static create(status, body, headers = {}) {
		ensure.signature(arguments, [ Number, String, [ undefined, Object ]]);
		return new HttpResponse(status, body, headers);
	}

	static createJsonResponse(status, body, headers) {
		ensure.signature(arguments, [ Number, Object, [ undefined, Object ]]);

		headers = {
			"content-type": "application/json",
			...headers,
		};
		return new HttpResponse(status, JSON.stringify(body), headers);
	}

	constructor(status, body, headers) {
		this.status = status;
		this.body = body;
		this.headers = { ...headers };
	}


};