"use strict";

const ensure = require("util/ensure");

module.exports = class HttpResponse {

	static create({ status, headers = {}, body }) {
		ensure.signature(arguments, [{
			status: Number,
			headers: [ undefined, Object ],
			body: String,
		}]);
		return new HttpResponse(status, headers, body);
	}

	static createJsonResponse({ status, headers = {}, body }) {
		ensure.signature(arguments, [{
			status: Number,
			headers: [ undefined, Object ],
			body: Object,
		}]);

		headers = {
			"content-type": "application/json",
			...headers,
		};
		return new HttpResponse(status, headers, JSON.stringify(body));
	}

	constructor(status, headers, body) {
		this.status = status;
		this.body = body;
		this.headers = { ...headers };
	}


};